    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double perimetro, area, diagonal;
             
        perimetro = Double.parseDouble(txtBase.getText()) * 2 + 
                Double.parseDouble(txtAltura.getText()) * 2;
        
        lblPerimetro.setText(String.valueOf(perimetro));
        
       area = Double.parseDouble(txtBase.getText()) * Double.parseDouble(txtAltura.getText());
        
       lblArea.setText(String.valueOf(area));       
       
       diagonal = Math.sqrt(Math.pow(Double.parseDouble(txtBase.getText()), 2) 
                    + Math.pow(Double.parseDouble(txtAltura.getText()), 2));
       
       lblDiagonal.setText(String.valueOf(diagonal)); 
       
        btnLimpar.setEnabled(true);
        btnCalcular.setEnabled(false);
    }//GEN-LAST:event_btnCalcularActionPerformed

